import React from 'react';
import Table from 'react-bootstrap/Table';
import "../../css/ManagePatients.css";
import Alert from 'react-bootstrap/Alert';
const ShowMyRequests = () => {
    const [myRequests, setMyRequests] = useState({
        loading: true,
        results: [],
        err: null,
        reload: 0
    })
    useEffect(() => {
        setHistory({ ...history, loading: true })
        axios.get("http://localhost:4000/Patient/listHistory/"+JSON.parse(localStorage.getItem("user")).id)
            .then((resp) => {
                setHistory({ ...history, results: resp.data, loading: false, err: null })
            })
            .catch(err => {
                setHistory({ ...history, loading: false, err: "something went wrong,please try again later!" })
            })
        // eslint-disable-next-line
    }, [history.reload]);
    return (
        <div className="manage-medicines p-5">
            <div className="header d-flex justify-content-between mb-5">
                <h3 className="text-center">My Requested Medicines</h3>
            </div>
            <Alert variant="danger" className='p-2'>
                This is simple alert
            </Alert>
            <Alert variant="success" className='p-2'>
                This is simple alert
            </Alert>
            <Table striped bordered hover >
                <thead>
                    <tr>
                        <th>MedicineID</th>
                        <th>PatientID</th>
                        <th>Request State</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>1</td>
                        <td>5</td>
                        <td>pending</td>
                    </tr>
                    <tr>
                        <td>1</td>
                        <td>5</td>
                        <td>pending</td>
                    </tr>
                </tbody>
            </Table>
        </div>
    );
};

export default ShowMyRequests;
