import React from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import { getAuthUser } from "../../helper/Storage";
import axios from "axios";
import { useParams } from "react-router-dom";
const UpdatePatients = () => {
    let { id } = useParams();
    const auth = getAuthUser();
    const [Patient, setPatient] = useState({
        name: "",
        email: "",
        phone: "",
        password: "",
        err: "",
        loading: false,
        reload: false,
        success: null,
        result: [],
    });
    const updatePat = (e) => {
        e.preventDefault();
        setMedicine({ ...medicine, loading: true });
        const formData = new FormData();
        formData.append("medicineName", medicine.medicineName);
        formData.append("medicineDesc", medicine.medicineDesc)
        formData.append("price", medicine.price)
        formData.append("expirationDate", medicine.expirationDate)
        formData.append("categoryID", medicine.categoryID)
        if (image.current.files && image.current.files[0]) {
            formData.append("image", image.current.files[0]);
        }
        axios
            .put("http://localhost:4000/medicines/update/" + id, formData, {
                headers: {
                    token: auth.token,
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((resp) => {
                setMedicine({
                    ...medicine,
                    loading: false,
                    success: "medicine updated successfully !",
                    reload: medicine.reload + 1,
                });
            })
            .catch((err) => {
                setMedicine({
                    ...medicine,
                    loading: false,
                    success: null,
                    err: "Something went wrong, please try again later !",
                });
            });
    };
    return (
        <div className="login-container">
            <h1>Update Patient form</h1>
            <Alert variant="danger" className='p-2'>
                This is simple alert
            </Alert>
            <Alert variant="success" className='p-2'>
                This is simple alert
            </Alert>
            <Form >
            <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Name" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Email" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Phone" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Password" />
                </Form.Group>
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Update Medicine
                </Button>
            </Form>
        </div>
    );
};

export default UpdatePatients;
