import React, { useEffect, useState } from 'react';
import Cards from '../../components/Cards';
import Form from 'react-bootstrap/Form';
import axios from 'axios';
const Home = () => {
    const [medicines,setMedicines]=useState({
        loading:true,
        results:[],
        err:null,
        reload:0
    })
    useEffect(()=>
    {
        
        axios.get("http://localhost:4000/medicines/list")  
        .then(resp=>
            {
                setMedicines({...medicines,loading:false})
            })
    },[])
    return (
        <div className="homa-container p-5">
            <Form>
                <Form.Group className="mb-3 d-flex">
                    <Form.Control type="text" placeholder="Search Medicines" className="rounded-0"/>
                    <button className="btn btn-dark rounded-0" >Search</button>
                </Form.Group>
                </Form>
                <div className="row">
                    <div className="col-3 card-movie-container">
                        <Cards />
                    </div>
                    <div className="col-3 card-movie-container">
                        <Cards />
                    </div>
                    <div className="col-3 card-movie-container">
                        <Cards />
                    </div>
                    <div className="col-3 card-movie-container">
                        <Cards />
                    </div>
                </div>
        </div>
    );
};
export default Home;
