import React,{useState} from 'react';
//import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import "../css/Cards.css"
import { Link } from 'react-router-dom';
import axios from 'axios';
const CardCategory = (props) => {
  const [categories, setCategories] = useState({
    loading: true,
    results: [],
    err: null,
    reload: 0
  })
  const listMedicinesOfAnyCategory = () => {
    axios.get("http://localhost:4000/Categories/listMedicinesOfAnyCategory/" + categoryID)
      .then((resp) => {
        setCategories({ ...categories, reload: categories.reload + 1 })
      })
      .catch(err => {
      })
  }
  return (
    <div>
      <Card >
        <Card.Img className="card-image" variant="top" src={props.image_url} />
        <Card.Body>
          <hr></hr>
          <Card.Title>{props.categoryName}</Card.Title><hr></hr>
          <Card.Text>{props.categoryDesc}</Card.Text><hr></hr>
          <Link className="btn btn-dark w-100" to={"/"} onClick={(e) => { listMedicinesOfAnyCategory(categories.categoryID) }}>show Medicines</Link>
        </Card.Body>
      </Card>
    </div>
  );
};
export default CardCategory;
