import React,{ useState,  useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import { getAuthUser } from "../../helper/Storage";
import axios from "axios";
import { useParams } from "react-router-dom";
const UpdatePatients = () => {
    let { id } = useParams();
    const auth = getAuthUser();
    const [patient, setPatient] = useState({
        name: "",
        email: "",
        phone: "",
        password: "",
        err: "",
        loading: false,
        reload: false,
        success: null,
        result: [],
    });
    const UpdatePatients = (e) => {
        e.preventDefault();
        setPatient({ ...patient, loading: true });
        const formData = new FormData();
        formData.append("name", patient.name);
        formData.append("email", patient.email)
        formData.append("phone", patient.phone)
        formData.append("password", patient.password)
        axios
            .put("http://localhost:4000/Patient/updatePatient/" + id, formData, {
                headers: {
                    token: auth.token,
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((resp) => {
                setPatient({
                    ...patient,
                    loading: false,
                    success: "patient updated successfully !",
                    reload: patient.reload + 1,
                });
            })
            .catch((err) => {
                setPatient({
                    ...patient,
                    loading: false,
                    success: null,
                    err: "Something went wrong, please try again later !",
                });
            });
    };
    useEffect(() => {
        axios.get("http://localhost:4000/Patient/showPatient/" + id)
            .then((result) => {
                setPatient({
                    ...patient,
                    name: result.data[0].name,
                    email: result.data[0].email,
                    phone: result.data[0].phone,
                    password: result.data[0].password,
                });
            })
            .catch((err) => {
                setPatient({
                    ...patient,
                    loading: false,
                    success: null,
                    err: "Something went wrong, please try again later !",
                });
            });
    },
     // eslint-disable-next-line
    [patient.reload]);
    return (
        <div className="login-container">
            <h1>Update Patient form</h1>
            {
                patient.err && (
                    <Alert variant="danger" className='p-2'>
                        {patient.err}
                    </Alert>
                )
            }
            {patient.success && (
                <Alert variant="success" className="p-2">
                    {patient.success}
                </Alert>
            )}
            <Form onSubmit={UpdatePatients} className="text-center py-2">
            <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Name"
                    value={patient.name}
                    onChange={(e) => setPatient
                        ({ ...patient, name: e.target.value })} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Email"
                    value={patient.email}
                    onChange={(e) => setPatient
                        ({ ...patient, em: e.target.value })} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Phone" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Password" />
                </Form.Group>
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Update Medicine
                </Button>
            </Form>
        </div>
    );
};

export default UpdatePatients;
