import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import "../../css/ManagePatients.css";
const Requests = () => {
    const [requests, setRequests] = useState({
        loading: true,
        results: [],
        err: null,
        reload: 0
    })
    useEffect(() => {
        setRequests({ ...requests, loading: true })
        axios.get("http://localhost:4000/medicines/showRequest")
            .then((resp) => {
                setRequests({ ...requests, results: resp.data, loading: false, err: null })
            })
            .catch(err => {
                setRequests({ ...requests, loading: false, err: "something went wrong,please try again later!" })
            })
        // eslint-disable-next-line
    }, [requests.reload]);
    return (
        <div className="manage-medicines p-5">
            <div className="header d-flex justify-content-between mb-5">
                <h3 className="text-center">Requested Medicines</h3>
            </div>
            <Table striped bordered hover >
                <thead>
                    <tr>
                        <th>patient</th>
                        <th>medicineName</th>
                        <th></th>
                        <th>Request State</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>

                    {
                        medicines.results.map((medicine) => (
                            <tr key={medicine.medicineID}>
                                <td>{medicine.medicineID}</td>
                                <td><img src={medicine.image_url} alt={medicine.medicineName} className="image-avatar" /></td>
                                <td>{medicine.medicineName}</td>
                                <td>{medicine.medicineDesc}</td>
                                <td>{medicine.price}</td>
                                <td>{medicine.expirationDate}</td>
                                <td>{medicine.categoryID}</td>
                                <td>
                                    <button className="btn btn-sm btn-danger"
                                    //    onClick={(e) => { deleteMedicine(medicine.medicineID) }}
                                    >Accept</button>
                                    <button className="btn btn-sm btn-danger"
                                    //    onClick={(e) => { deleteMedicine(medicine.medicineID) }}
                                    >decline</button>                </td>
                            </tr>
                        ))}
                </tbody>
                <tbody>
                    <tr>
                        <td>menna@gmail.com</td>
                        <td>profine</td>
                        <td>image</td>
                        <td>pending</td>
                        <td>
                            <button className="btn btn-sm btn-success">Accept</button>
                            <button className="btn btn-sm btn-danger mx-2">Decline</button>
                        </td>
                    </tr>
                </tbody>
            </Table>
        </div>
    );
};
export default Requests;
