import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
//import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import { getAuthUser } from "../../helper/Storage";
const AddMedicine = () => {
    const [medicine, setMedicine] = useState({
        medicineName:'',
        medicineDesc:'',
        price:'',
        expirationDate:'',
        categoryID:'',
        loading: false,
        err: [],
      })
    return (
        <div className="login-container">
            <h1>Add Medicine Form</h1>
            {/* <Alert variant="danger" className='p-2'>
                This is simple alert
            </Alert>
            <Alert variant="success" className='p-2'>
                This is simple alert
            </Alert> */}
            <Form >
                <Form.Group className="mb-3">
                    <Form.Control value={medicine.medicineName} onChange={(e)=>setMedicine({...medicine,medicineName:e.tar})} type="text" placeholder="MedicineName" />
                </Form.Group>

                <Form.Group className="mb-3"><textarea className="form-control" placeholder="Description" value={medicine.medicineDesc} rows={3}></textarea>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Price" value={medicine.price} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="ExpirationDate" value={medicine.expirationDate} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="CategoryID" value={medicine.categoryID}/>
                </Form.Group>

                <Form.Group className="mb-3">
                    <input type="file" className="form-control" />
                </Form.Group>
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Add Medicine
                </Button>
            </Form>
        </div>
    );
};

export default AddMedicine;
