import React from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import { getAuthUser } from "../../helper/Storage";
const AddPatient = () => {
    const auth = getAuthUser();
    const [patient, setPatient] = useState({
        name: '',
        email: '',
        phone: '',
        password: '',
        loading: false,
        err: "",
        success: null
    })
    
    const createPatient = (e) => {
        e.preventDefault();
        setPatient({ ...patient, loading: true });
        const formData = new FormData();
        formData.append("name", patient.name);
        formData.append("email", patient.email)
        formData.append("phone", patient.phone)
        formData.append("password", patient.password)
        console.log(formData)
        axios.post("http://localhost:4000/Patient/addPatient", formData,
            {
                headers: {
                    token: auth.token,
                    "Content-Type": "multipart/form-data"
                }
            })
            .then((resp) => {
                setPatient({
                    name: '',
                    email: '',
                    phone: '',
                    password: '',
                   
                    loading: false,
                    err:null,
                    success: "Patient created successfully!"
                });
                
            })
            .catch(err => {
                setPatient({
                    ...patient,
                    loading: false,
                    success: null,
                    err: "Something went wrong"
                });
            })
    }
    return (
        <div className="login-container">
            <h1>Add Patient Form</h1>
            {
                patient.err && (
                    <Alert variant="danger" className='p-2'>
                        {patient.err}
                    </Alert>
                )
            }
            {
                patient.success && (
                    <Alert variant="success" className='p-2'>
                        {patient.success}
                    </Alert>
                )
            }
            <Form onSubmit={createPatient}>
                <Form.Group className="mb-3">
                    <Form.Control value={patient.name}
                        required onChange={(e) => setPatient({ ...patient, name: e.target.value })} type="text" placeholder="name" />
                </Form.Group>


                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="email"
                        value={patient.email}
                        required
                        onChange={(e) => setPatient({ ...patient, email: e.target.value })} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="phone" 
                        value={patient.phone}
                        required
                        onChange={(e) => setPatient({ ...patient, phone: e.target.value })} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="password" 
                        value={patient.password}
                        required
                        onChange={(e) => setPatient({ ...patient, password: e.target.value })} />
                </Form.Group>

         
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Add Patient
                </Button>
            </Form>
        </div>
    );
};
export default  AddPatient ;
