import React, { useEffect, useState } from 'react';
import Cards from '../../components/Cards';
import Form from 'react-bootstrap/Form';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';

const Home = () => {
    const [medicines, setMedicines] = useState({
        loading: true,
        results: [],
        err: null,
        reload: 0
    })
    useEffect(() => {
        setMedicines({ ...medicines, loading: true })
        axios.get("http://localhost:4000/medicines/list")
            .then(resp => {
                console.log(resp)
                setMedicines({ ...medicines, results: resp.data, loading: false, err: null })
            })
            .catch(err => {
                setMedicines({ ...medicines, loading: false, err: "something went wrong,please try again later!" })
            })
    }, []);
    return (
        <div className="homa-container p-5">
            {
                medicines.loading
            }
            <Spinner animation="border" role="status">
                <span className="visually-hidden">Loading...</span>
            </Spinner>
            <Form>
                <Form.Group className="mb-3 d-flex">
                    <Form.Control type="text" placeholder="Search Medicines" className="rounded-0" />
                    <button className="btn btn-dark rounded-0" >Search</button>
                </Form.Group>
            </Form>
            <div className="row">
                <div className="col-3 card-movie-container">
                    <Cards />
                </div>
                <div className="col-3 card-movie-container">
                    <Cards />
                </div>
                <div className="col-3 card-movie-container">
                    <Cards />
                </div>
                <div className="col-3 card-movie-container">
                    <Cards />
                </div>
            </div>
        </div>
    );
};
export default Home;
