import React,{useState} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import { getAuthUser } from "../../helper/Storage";
const AddPatients = () => {
    const auth = getAuthUser();
    const [medicine, setMedicine] = useState({
        medicineName: '',
        medicineDesc: '',
        price: '',
        expirationDate: '',
        categoryID: '',
        loading: false,
        err: "",
        success: null
    })
    const image = useRef(null);
    const createMedicine = (e) => {
        e.preventDefault();
        setMedicine({ ...medicine, loading: true });
        const formData = new FormData();
        formData.append("medicineName", medicine.medicineName);
        formData.append("medicineDesc", medicine.medicineDesc)
        formData.append("price", medicine.price)
        formData.append("expirationDate", medicine.expirationDate)
        formData.append("categoryID", medicine.categoryID)
        if (image.current.files && image.current.files[0]) {
            formData.append("image", image.current.files[0])
        }
        console.log(formData)
        axios.post("http://localhost:4000/medicines/create", formData,
            {
                headers: {
                    token: auth.token,
                    "Content-Type": "multipart/form-data"
                }
            })
            .then((resp) => {
                setMedicine({
                    medicineName: '',
                    medicineDesc: '',
                    price: '',
                    expirationDate: '',
                    categoryID: '',
                    loading: false,
                    err:null,
                    success: "Medicine created successfully!"
                });
                image.current.value = null
            })
            .catch(err => {
                setMedicine({
                    ...medicine,
                    loading: false,
                    success: null,
                    err: "Something went wrong"
                });
            })
    }
    return (
        <div className="login-container">
            <h1>Add Patient Form</h1>
            {
                patient.err && (
                    <Alert variant="danger" className='p-2'>
                        {patient.err}
                    </Alert>
                )
            }
            {
                patient.success && (
                    <Alert variant="success" className='p-2'>
                        {patient.success}
                    </Alert>
                )
            }
            <Form
                onSubmit={createPatient}
            >
                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Full Name"
                        value={patient.name}
                        onChange={(e) => setPatient({ ...patient, name: e.target.value })}
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Control type="email" placeholder="Email"
                        value={patient.email}
                        onChange={(e) => setPatient({ ...patient, email: e.target.value })}
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Control type="phone" placeholder="Phone"
                        value={patient.phone}
                        onChange={(e) => setPatient({ ...patient, phone: e.target.value })}
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Control type="password" placeholder="Password"
                        value={patient.password}
                        onChange={(e) => setPatient({ ...patient, password: e.target.value })}
                    />
                </Form.Group>
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Add Patient
                </Button>
            </Form>
        </div>
    );
};
export default AddPatients;
