import React, { useState, useRef, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import { getAuthUser } from "../../helper/Storage";
import axios from "axios";
import { useParams } from "react-router-dom";
const UpdateCategory = () => {
    let { id } = useParams();
    const auth = getAuthUser();
    const [category, setCategory] = useState({
        categoryName: "",
        categoryDesc: "",
        image: null,
        err: "",
        loading: false,
        reload: false,
        success: null,
        result: [],
    });
    const image = useRef(null);
    const updateCategory = (e) => {
        e.preventDefault();
        setCategory({ ...cat, loading: true });
        const formData = new FormData();
        formData.append("medicineName", medicine.medicineName);
        formData.append("medicineDesc", medicine.medicineDesc)
        formData.append("price", medicine.price)
        formData.append("expirationDate", medicine.expirationDate)
        formData.append("categoryID", medicine.categoryID)
        if (image.current.files && image.current.files[0]) {
            formData.append("image", image.current.files[0]);
        }
        axios
            .put("http://localhost:4000/medicines/update/" + id, formData, {
                headers: {
                    token: auth.token,
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((resp) => {
                setMedicine({
                    ...medicine,
                    loading: false,
                    success: "medicine updated successfully !",
                    reload: medicine.reload + 1,
                });
            })
            .catch((err) => {
                setMedicine({
                    ...medicine,
                    loading: false,
                    success: null,
                    err: "Something went wrong, please try again later !",
                });
            });
    };
    return (
        <div className="login-container">
            <h1>Update Category form</h1>
            <Alert variant="danger" className='p-2'>
                This is simple alert
            </Alert>
            <Alert variant="success" className='p-2'>
                This is simple alert
            </Alert>
            <Form >
                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="CategoryName" />
                </Form.Group>
                <Form.Group className="mb-3">
                <textarea
                        className="form-control"
                        placeholder="Description"
                        rows={3}></textarea>
                </Form.Group>
                <Form.Group className="mb-3">
                    <input type="file" className="form-control" />
                </Form.Group>
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Update Category
                </Button>
            </Form>
        </div>
    );
};

export default UpdateCategory;
