                    <button className="btn btn-dark rounded-0" >Search</button>
                </Form.Group>
                </Form> */}
                <div className="row">
                    <div className="col-3 card-movie-container">
                        <CardCategory />
                    </div>
                    <div className="col-3 card-movie-container">
                        <CardCategory />
                    </div>
                    <div className="col-3 card-movie-container">
                        <CardCategory />
                    </div>
                    <div className="col-3 card-movie-container">
                        <CardCategory />
                    </div>
                </div>
        </div>
    );
};
export default Category;
