import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import { getAuthUser } from '../../helper/Storage';
import axios from 'axios';

const ListSearch = () => {
    const auth = getAuthUser();
    const [history, setHistory] = useState({
        loading: true,
        results: [],
        err: null,
        reload: 0
    })
    useEffect(() => {
        setHistory({ ...history, loading: true })
        axios.get("http://localhost:4000/Categories/listCategories")
            .then((resp) => {
                setHistory({ ...history, results: resp.data, loading: false, err: null })
            })
            .catch(err => {
                setHistory({ ...history, loading: false, err: "something went wrong,please try again later!" })
            })
        // eslint-disable-next-line
    }, [categories.reload]);
    // const deleteHistory = (categoryID) => {
    //     axios.delete("http://localhost:4000/Categories/deleteCategory/" + categoryID, {
    //         headers: {
    //             token: auth.token
    //         }
    //     })
    //         .then((resp) => {
    //             setCategories({ ...history, reload: categories.reload + 1 })
    //         })
    //         .catch(err => {
    //         })
    // }
    return (
        <div className="manage-medicines p-5">
            <Table striped bordered hover >
                <tbody>

                    {
                        history.results.map((medicine) => (
                            <tr key={medicine.medicineID}>
                                <td>{medicine.medicineID}</td>
                                <td><img src={medicine.image_url} alt={medicine.medicineName} className="image-avatar" /></td>
                                <td>{medicine.medicineName}</td>
                                <td>{medicine.medicineDesc}</td>
                                <td>{medicine.price}</td>
                                <td>{medicine.expirationDate}</td>
                                <td>{medicine.categoryID}</td>
                                <td>
                                    <button className="btn btn-sm btn-danger"
                                     onClick={(e) => { deleteHistory(medicine.medicineID) }}
                                     >Delete</button>
                                </td>
                            </tr>
                        ))}
                </tbody>
            </Table>
        </div>
    );
};

export default ListSearch;
