import React from 'react';
import Table from 'react-bootstrap/Table';
import "../../css/ManagePatients.css";
const Requests = () => {
    const auth = getAuthUser();
    const [requests, setRequests] = useState({
        loading: true,
        results: [],
        err: null,
        reload: 0
    })
    useEffect(() => {
        setRequests({ ...medicines, loading: true })
        axios.get("http://localhost:4000/medicines/list")
            .then((resp) => {
                setRequests({ ...medicines, results: resp.data, loading: false, err: null })
            })
            .catch(err => {
                setRequests({ ...medicines, loading: false, err: "something went wrong,please try again later!" })
            })
        // eslint-disable-next-line
    }, [medicines.reload]);
    const deleteMedicine = (medicineID) => {
        axios.delete("http://localhost:4000/medicines/delete/" + medicineID, {
            headers: {
                token: auth.token
            }
        })
            .then((resp) => {
                setMedicines({ ...medicines, reload: medicines.reload + 1 })
            })
            .catch(err => {
            })
    }
    return (
        <div className="manage-medicines p-5">
            <div className="header d-flex justify-content-between mb-5">
                <h3 className="text-center">Requested Medicines</h3>
            </div>
            <Table striped bordered hover >
                <thead>
                    <tr>
                        <th>patient</th>
                        <th>medicineName</th>
                        <th></th>
                        <th>Request State</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>menna@gmail.com</td>
                        <td>profine</td>
                        <td>image</td>
                        <td>pending</td>
                        <td>
                            <button className="btn btn-sm btn-success">Accept</button>
                            <button className="btn btn-sm btn-danger mx-2">Decline</button>
                        </td>
                    </tr>
                </tbody>
            </Table>
        </div>
    );
};
export default Requests;
