import React, { useState, useRef } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import { getAuthUser } from "../../helper/Storage";
const AddPatients = () => {
    const auth = getAuthUser();
    const [patient, setPatient] = useState({
        name: '',
        email: '',
        password: '',
        phone: '',
        loading: false,
        err: "",
        success: null
    })
    const createPatient = (e) => {
        e.preventDefault();
        setPatient({ ...patient, loading: true });
        const formData = new FormData();
        formData.append("name", patient.name);
        formData.append("email", medicine.email)
        formData.append("password", medicine.password)
        formData.append("phone", medicine.phone)
        formData.append("categoryID", medicine.categoryID)
        if (image.current.files && image.current.files[0]) {
            formData.append("image", image.current.files[0])
        }
        console.log(formData)
        axios.post("http://localhost:4000/medicines/create", formData,
            {
                headers: {
                    token: auth.token,
                    "Content-Type": "multipart/form-data"
                }
            })
            .then((resp) => {
                setMedicine({
                    medicineName: '',
                    medicineDesc: '',
                    price: '',
                    expirationDate: '',
                    categoryID: '',
                    loading: false,
                    err:null,
                    success: "Medicine created successfully!"
                });
                image.current.value = null
            })
            .catch(err => {
                setMedicine({
                    ...medicine,
                    loading: false,
                    success: null,
                    err: "Something went wrong"
                });
            })
    }
    return (
        <div className="login-container">
            <h1>Add Patient Form</h1>
            <Alert variant="danger" className='p-2'>
                This is simple alert
            </Alert>
            <Alert variant="success" className='p-2'>
                This is simple alert
            </Alert>
            <Form >
                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Name" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Email" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Phone" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Password" />
                </Form.Group>
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Add Patient
                </Button>
            </Form>
        </div>
    );
};

export default AddPatients;
