import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import "../../css/MedicineDetails.css"
import axios from 'axios';
//import RequestMedicine from "../../components/RequestMedicine"
const DetailsMedicine = () => {
    let { id } = useParams();
    const [medicine, setMedicine] = useState({
        loading: true,
        result: [],
        err: null,
    })
    useEffect(() => {
        setMedicine({ ...medicine, loading: true })
        axios.get("http://localhost:4000/medicines/show/" + id )
            .then(resp => {
                setMedicine({ ...medicine, 
                    result: resp.data, loading: false,
                     err: null })
            })
            .catch((err) => {
                setMedicine({ ...medicine, loading: false,
                     err: "something went wrong,please try again later!" })
            })
        // eslint-disable-next-line
    }, []);
    return (
        <div className="medicine-details-container p-5">
            <div className="row">
                <div className="col-3">
                    <img className="medicine-image" src={medicine.result.image_url} alt={medicine.result.medicineName} />
                </div>
                <div className="col-9">
                    <h3>{medicine.result.medicineName}</h3>
                    <p>
                    {medicine.result.medicineDesc}
                    </p>
                    <h5>{medicine.result.price}</h5>
                    <h5>{medicine.result.expirationDate}</h5>
                    <h5>{medicine.result.categoryID}</h5>
                </div>
            </div>
            <hr />
            <button className="text-center bg-dark text-white p-2"><h5>Request Medicines</h5></button>
        </div>
    );
};

export default DetailsMedicine;
