import React from 'react';
import CardCategory from '../../components/CardCategory';
//import Form from 'react-bootstrap/Form';
const Category = () => {
    const auth = getAuthUser();
    const [medicines, setMedicines] = useState({
        loading: true,
        results: [],
        err: null,
        reload: 0
    })
    const [searchTerm, setsearchTerm] = useState("");
    useEffect(() => {
        setMedicines({ ...medicines, loading: true });
        axios.get("http://localhost:4000/Medicines/list",
            {
                params: {
                    searchTerm: searchTerm,
                },
            },
        )
            .then((resp) => {
                console.log(resp);
                setMedicines({ ...medicines, results: resp.data, loading: false, err: null });
            })
            .catch((err) => {
                setMedicines({
                    ...medicines,
                    loading: false,
                    err: " something went wrong, please try again later ! ",
                });
            });
    },// eslint-disable-next-line 
        [medicines.reload]);
    const Search = (e) => {
        e.preventDefault();
        setMedicines({ ...medicines, reload: medicines.reload + 1 });
        axios.post("http://localhost:4000/Patient/search", {
            searchTerm: searchTerm,
            user_id: JSON.parse(localStorage.getItem("user")).id,
        })
            .then((resp) => { console.log(resp.data) })
            .catch((error) => {
                console.error(error);
            })
    };
    return (
        <div className="homa-container p-5">
            {/* <Form>
                <Form.Group className="mb-3 d-flex">
                    <Form.Control type="text" placeholder="Search Category" className="rounded-0"/>
                    <button className="btn btn-dark rounded-0" >Search</button>
                </Form.Group>
                </Form> */}
                <div className="row">
                    <div className="col-3 card-movie-container">
                        <CardCategory />
                    </div>
                    <div className="col-3 card-movie-container">
                        <CardCategory />
                    </div>
                    <div className="col-3 card-movie-container">
                        <CardCategory />
                    </div>
                    <div className="col-3 card-movie-container">
                        <CardCategory />
                    </div>
                </div>
        </div>
    );
};
export default Category;
