import React, { useEffect, useState } from 'react';
import Cards from '../../components/Cards';
import Form from 'react-bootstrap/Form';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import Alert from 'react-bootstrap/Alert';
import { Link } from 'react-router-dom';
const Home = () => {
    const [medicines, setMedicines] = useState({
        loading: true,
        results: [],
        err: null,
        reload: 0
    })
    const [searchTerm, setsearchTerm] = useState("");
    const filteredResults = medicines.results.filter((medicines) =>
    medicines.name.toLowerCase().includes(searchTerm.toLowerCase())
  );
    const Search = () => {
        setMedicines({ ...medicines, loading: true });
        axios
            .get("http://localhost:4000/medicines/list",
                {
                    params: {
                        searchTerm: searchTerm,
                    },

                },
            )
            .then((resp) => {
                console.log(resp);
                setMedicines({ ...medicines, results: resp.data, loading: false, err: null });
                axios.post("http://localhost:4000/Patient/search", {
                    searchTerm: searchTerm,
                    user_id: JSON.parse(localStorage.getItem("user")).id,
                })
                    .then((resp) => { console.log(resp.data) })

                    .catch((error) => {
                        console.error(error);
                    })
            })
            .catch((err) => {
                setMedicines({
                    ...medicines,
                    loading: false,
                    err: " something went wrong, please try again later ! ",
                });
            });
    };
    useEffect(() => {
        setMedicines({ ...medicines, loading: true });
        axios
            .get("http://localhost:4000/medicines/list",
                {
                    // params: {
                    //   searchTerm : searchTerm ,
                    // },

                },
                // {
                //    headers:{
                //     token: auth.token,
                //   }
                // }


            )
            .then((resp) => {
                console.log(resp);
                setMedicines({ ...medicines, results: resp.data, loading: false, err: null });
            })
            .catch((err) => {
                setMedicines({
                    ...medicines,
                    loading: false,
                    err: " something went wrong, please try again later ! ",
                });
            });
    }, []);
    return (
        <div className="home-container p-5">
          {/* Loader  */}
          {medicines.loading === true && (
            <div className="text-center">
              <Spinner animation="border" role="status">
                <span className="visually-hidden">Loading...</span>
              </Spinner>
            </div>
          )}
    
          {/* LIST Medicines  */}
          {medicines.loading === false && medicines.err == null && (
            <>
              {/* Filter  */}
              <Form onSubmit={Search}>
                <Form.Group className="mb-3 d-flex">
                  <Form.Control
                    type="text"
                    placeholder="Search Medicines"
                    className="rounded-0"
                    required
                    value={searchTerm}
                    onChange={(e) => setsearchTerm (e.target.value)}
                  />
                  
                </Form.Group>
              </Form>
              
             
    
              {/* LIST Medicines  */}
              <div className="row ">
                {filteredResults.map((medicine) => (
                  <div className="col-3 card-movie-container" key={medicine.id}>
                    <Cards
                      name={medicine.name}
                      image={medicine.image_url}
                      id={medicine.id}
                      // description={medicine.description}
                      price={medicine.price}
                      expirationDate={medicine.expirationDate}
                      category_id={medicine.category_id}
                    />
                  </div>
                ))}
              </div>
            </>
          )}
    
          {/* ERRORS HANDLING  */}
          {medicines.loading === false && medicines.err != null && (
            <Alert variant="danger" className="p-2">
              {medicines.err}
            </Alert>
          )}
    
          {medicines.loading === false &&
            medicine.err == null &&
            medicine.results.length === 0 && (
              <Alert variant="info" className="p-2">
                No Medicines, please try again later !
              </Alert>
            )}
        </div>
      );
    };
export default Home;
