import React, { useState, useRef } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import { getAuthUser } from "../../helper/Storage";
const UpdateMedicine = () => {
    const [medicine, setMedicine] = useState({
        medicineName: '',
        medicineDesc: '',
        price: '',
        expirationDate: '',
        categoryID: '',
        image:null,
        loading: false,
        err: "",
        success: null
    })
    const image = useRef(null);
    const updateMedicine = (e) => {
        e.preventDefault();
        setMedicine({ ...medicine, loading: true });
        const formData = new FormData();
        formData.append("medicineName", medicine.medicineName);
        formData.append("medicineDesc", medicine.medicineDesc)
        formData.append("price", medicine.price)
        formData.append("expirationDate", medicine.expirationDate)
        formData.append("categoryID", medicine.categoryID)
        if (image.current.files && image.current.files[0]) {
            formData.append("image", image.current.files[0])
        }
        console.log(formData)
        axios.put("http://localhost:4000/medicines/update/", formData,
            {
                headers: {
                    token: auth.token,
                    "Content-Type": "multipart/form-data"
                }
            })
            .then((resp) => {
                setMedicine({
                    medicineName: '',
                    medicineDesc: '',
                    price: '',
                    expirationDate: '',
                    categoryID: '',
                    loading: false,
                    err:null,
                    success: "Medicine created successfully!"
                });
                image.current.value = null
            })
            .catch(err => {
                setMedicine({
                    ...medicine,
                    loading: false,
                    success: null,
                    err: "Something went wrong"
                });
            })
    }
    return (
        <div className="login-container">
            <h1>Update Medicine form</h1>
            <Alert variant="danger" className='p-2'>
                This is simple alert
            </Alert>
            <Alert variant="success" className='p-2'>
                This is simple alert
            </Alert>
            <Form >
            <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="MedicineName" />
                </Form.Group>

                <Form.Group className="mb-3"><textarea className="form-control" placeholder="Description" rows={3}></textarea>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Price" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="ExpirationDate" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="CategoryID" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <input type="file" className="form-control" />
                </Form.Group>
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Update Medicine
                </Button>
            </Form>
        </div>
    );
};

export default UpdateMedicine;
