import React, { useEffect, useState } from 'react';
import "../../css/MedicineDetails.css"
import axios from 'axios';
//import RequestMedicine from "../../components/RequestMedicine"
const DetailsMedicine = () => {
    const [medicine, setMedicine] = useState({
        loading: true,
        result: null,
        err: null,
    })
    useEffect(() => {
        setMedicine({ ...medicine, loading: true })
        axios.get("http://localhost:4000/medicines/list")
            .then(resp => {
                setMedicine({ ...medicines, results: resp.data, loading: false, err: null })
            })
            .catch(err => {
                setMedicine({ ...medicines, loading: false, err: "something went wrong,please try again later!" })
            })
        // eslint-disable-next-line
    }, [medicines.reload]);
    return (
        <div className="medicine-details-container p-5">
            <div className="row">
                <div className="col-3">
                    <img className="medicine-image" src="https://picsum.photos/200/300" alt="" />
                </div>
                <div className="col-9">
                    <h3>Medicine Title</h3>
                    <p>
                        Lorem Ipsum is simply dummy text of the printing and typesetting
                        industry. Lorem Ipsum has been the industry's standard dummy text
                        ever since the 1500s, when an unknown printer took a galley of type
                        and scrambled it to make a type specimen book. It has survived not
                        only five centuries, but also the leap into electronic typesetting,
                        remaining essentially unchanged. It was popularised in the 1960s
                        with the release of Letraset sheets containing Lorem Ipsum passages,
                        and more recently with desktop publishing software like Aldus
                        PageMaker including versions of Lorem Ipsum.
                    </p>
                </div>
            </div>
            <hr />
            <button className="text-center bg-dark text-white p-2"><h5>Request Medicines</h5></button>
            {/* <RequestMedicine />
            <RequestMedicine />
            <RequestMedicine /> */}
        </div>
    );
};

export default DetailsMedicine;
