import React, { useEffect, useState } from 'react';
import Cards from '../../components/Cards';
import Form from 'react-bootstrap/Form';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import Alert from 'react-bootstrap/Alert';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { getAuthUser } from '../../helper/Storage';
const Home = () => {
    let{id}=useParams();
    const auth=getAuthUser()
    const [medicines, setMedicines] = useState({
        key:"",
        loading: true,
        results: [],
        err: null,
        reload: 0
    })
    useEffect(() => {
        setMedicines({ ...medicines, loading: true })
        // axios.get("http://localhost:4000/medicines/list", {
        //     params: {
        //         search: search,
        //     }
        // })
            .then(resp => {
                console.log(resp)
                setMedicines({ ...medicines, results: resp.data, loading: false, err: null })
            })
            .catch(err => {
                setMedicines({ ...medicines, loading: false, err: "something went wrong,please try again later!" })
            })
        // eslint-disable-next-line
    }, [medicines.reload]);
    const SearchMedicines = (e) => {
        e.preventDefault()
        setMedicines({ ...medicines, reload: medicines.reload + 1 });
        axios.post("http://localhost:4000/Patient/search/"+id, { key: medicines.name }, {
            headers:
            {
                token: auth.token,
            }
        },
        )
            .then(res => {
                setMedicines({
                    key:"",
                    loading: false,
                    err:null,
                    success: "Patient created successfully!"
                });
            })
            .catch(err => {
                setMedicines({
                    ...medicines,
                    loading: false,
                    success: null,
                    err: "Something went wrong"
                });
            });
    }
    return (
        <div className="homa-container p-5">
            {
                medicines.loading === true && (
                    <div className="text-center">
                        <Spinner animation="border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </Spinner>
                    </div>
                )
            }
            {
                medicines.loading === false && medicines.err === null && (
                    <>
                        <Form onSubmit={SearchMedicines}>
                            <Form.Group className="mb-3 d-flex">
                                <Form.Control type="text"
                                    required
                                    placeholder="Search Medicines"
                                    className="rounded-0"
                                    value={medicines}
                                    onChange={(e) => setMedicines(e.target.value)} />
                                <button className="btn btn-dark rounded-0" >Search</button>
                            </Form.Group>
                        </Form>
                        <Form onSubmit={SearchMedicines}>
                            <Form.Group className="mb-3 d-flex">
                                <Link to={"/ListSearch"}><button className="btn btn-dark rounded-0" >history</button></Link>
                            </Form.Group>
                        </Form>
                        <div className="row">
                            {medicines.results.map((medicine) =>
                            (
                                <div className="col-3 card-movie-container" key={medicine.medicineID}>
                                    <Cards
                                        medicineName={medicine.medicineName}
                                        medicineDesc={medicine.medicineDesc}
                                        price={medicine.price}
                                        expirationDate={medicine.expirationDate}
                                        categoryID={medicine.categoryID}
                                        image_url={medicine.image_url}
                                        medicineID={medicine.medicineID}
                                    />
                                </div>
                            ))}

                        </div>

                    </>
                )
            }
            {
                medicines.loading === false && medicines.err !== null && (
                    <Alert variant="danger" className='p-2'>
                        {medicines.err}
                    </Alert>
                )} 
            {
                medicines.loading === false && medicines.err === null && medicines.results.length === 0 && (
                    <Alert variant="info" className='p-2'>
                        NOT FOUND!
                    </Alert>
                )}
        </div>
    );
};
export default Home;
