import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import "../../css/ManageCategories.css";
import {Link} from "react-router-dom"
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import { getAuthUser } from "../../helper/Storage";
const ManageCategories = () => {
  const auth = getAuthUser();
  const [categories, setCategories] = useState({
    loading: true,
    results: [],
    err: null,
    reload: 0
  })
  useEffect(() => {
    setCategories({ ...categories, loading: true })
    axios.get("http://localhost:4000/Categories/listCategories")
      .then((resp) => {
        setCategories({ ...categories, results: resp.data, loading: false, err: null })
      })
      .catch(err => {
        setCategories({ ...categories, loading: false, err: "something went wrong,please try again later!" })
      })
    // eslint-disable-next-line
  }, [categories.reload]);
  const deleteCategory = (categoryID) => {
    axios.delete("http://localhost:4000/Categories/deleteCategory/" + categoryID, {
      headers: {
        token: auth.token
      }
    })
      .then((resp) => {
        setCategories({ ...categories, reload: categories.reload + 1 })
      })
      .catch(err => {
      })
  }
    return (
        <div className="manage-medicines p-5">
            <div className="header d-flex justify-content-between mb-5">
            <h3 className="text-center">Manage Categories</h3>
            <Link to={"add"} className="btn btn-success">+Add Category</Link>
            </div>
            {/* <Alert variant="danger" className='p-2'>
                This is simple alert
            </Alert>
            <Alert variant="success" className='p-2'>
                This is simple alert
            </Alert> */}
            <Table striped bordered hover >
      <thead>
        <tr>
          <th>CategoryID</th>
          <th>Image</th>
          <th>CategoryName</th>
          <th>Description</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
      {
            categories.results.map((Category) => (
              <tr key={Category.medicineID}>
                <td>{medicine.medicineID}</td>
                <td><img src={medicine.image_url} alt={medicine.medicineName} className="image-avatar" /></td>
                <td>{medicine.medicineName}</td>
                <td>{medicine.medicineDesc}</td>
                <td>{medicine.price}</td>
                <td>{medicine.expirationDate}</td>
                <td>{medicine.categoryID}</td>
                <td>
                  <button className="btn btn-sm btn-danger" onClick={(e) => { deleteMedicine(medicine.medicineID) }}>Delete</button>
                  <Link to={"" + medicine.medicineID} className="btn btn-sm btn-primary mx-2">Update</Link>
                  <Link to={"/" + medicine.medicineID} className="btn btn-sm btn-info">Show</Link>
                </td>
              </tr>
            ))}
      </tbody>
    </Table>
        </div>
    );
};

export default ManageCategories;
