import React, { useState, useRef } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import { getAuthUser } from "../../helper/Storage";
import { useParams } from 'react-router-dom';
const UpdateMedicine = () => {
    const auth = getAuthUser();
    let{id}=useParams();
    const [medicine, setMedicine] = useState({
        medicineName: '',
        medicineDesc: '',
        price: '',
        expirationDate: '',
        categoryID: '',
        image:null,
        loading: false,
        err: "",
        success: null
    })
    const image = useRef(null);
    const updateMedicine = (e) => {
        e.preventDefault();
        setMedicine({ ...medicine, loading: true });
        const formData = new FormData();
        formData.append("medicineName", medicine.medicineName);
        formData.append("medicineDesc", medicine.medicineDesc)
        formData.append("price", medicine.price)
        formData.append("expirationDate", medicine.expirationDate)
        formData.append("categoryID", medicine.categoryID)
        if (image.current.files && image.current.files[0]) {
            formData.append("image", image.current.files[0])
        }
        console.log(formData)
        axios.put("http://localhost:4000/medicines/update/"+id, formData,
            {
                headers: {
                    token: auth.token,
                    "Content-Type": "multipart/form-data"
                }
            })
            .then((resp) => {
                
            })
            .catch(err => {
                setMedicine({
                    ...medicine,
                    loading: false,
                    success: null,
                    err: "Something went wrong"
                });
            })
    }
    return (
        <div className="login-container">
            <h1>Update Medicine form</h1>
            {
                medicine.err && (
                    <Alert variant="danger" className='p-2'>
                        {medicine.err}
                    </Alert>
                )
            }
            {
                medicine.success && (
                    <Alert variant="success" className='p-2'>
                        {medicine.success}
                    </Alert>
                )
            }
            <Form onSubmit={updateMedicine}>
            <Form.Group className="mb-3">
                    <Form.Control 
                    type="text" 
                    placeholder="MedicineName" 
                    value={medicine.medicineName}
                     onChange={(e) => setMedicine({ ...medicine, medicineName: e.target.value })}/>
                </Form.Group>

                <Form.Group className="mb-3">
                    <textarea className="form-control"
                     placeholder="Description" 
                     value={medicine.medicineDesc}
                    onChange={(e) => setMedicine({ ...medicine, medicineDesc: e.target.value })}
                     rows={3}></textarea>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Price" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="ExpirationDate" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="CategoryID" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <input type="file" className="form-control" />
                </Form.Group>
                <Button className="btn btn-dark w-100" variant="primary" type="submit">
                    Update Medicine
                </Button>
            </Form>
        </div>
    );
};

export default UpdateMedicine;
